package main

import "fmt"



func main()  {
	x := test(insert())
	fmt.Println(x)
	
}

func test(y string) int {
	count := 0
	for i, x := range y {
		if string(x) == "(" {
			count++
		} else if string(x) == ")" {
			count--
		}

		if count < 0 {
			return i + 1
		}
	}
	return count
}

